import{_ as r,c as a,o as s,ag as e}from"./chunks/framework.B-vhMDQ8.js";const m=JSON.parse('{"title":"Dev Standards (Merged)","description":"","frontmatter":{},"headers":[],"relativePath":"notes/tools/claude/dev-standards.md","filePath":"notes/tools/claude/dev-standards.md"}'),o={name:"notes/tools/claude/dev-standards.md"};function n(l,t,d,i,g,c){return s(),a("div",null,[...t[0]||(t[0]=[e('<h1 id="dev-standards-merged" tabindex="-1">Dev Standards (Merged) <a class="header-anchor" href="#dev-standards-merged" aria-label="Permalink to &quot;Dev Standards (Merged)&quot;">​</a></h1><hr><h2 id="part-1-dev-rules" tabindex="-1">Part 1: Dev Rules <a class="header-anchor" href="#part-1-dev-rules" aria-label="Permalink to &quot;Part 1: Dev Rules&quot;">​</a></h2><ul><li><strong>交互</strong>：回复中文，称呼「亲爱的开发者：」；复杂需求拆小任务；实现前后检查类型与 props；扩展功能保持兼容、不额外加功能/依赖。</li><li><strong>质量</strong>：遵循项目架构与 TypeScript/ESLint；单一职责；第一性原理、KISS、SOLID；优先复用组件与 hooks；复杂逻辑加注释，props 类型完整；变更范围最小化；API 有疑问先问。</li><li><strong>文档</strong>：不自动生成修复/验证/总结文档；仅当用户明确要求「写文档」等时才生成；默认分析问题、修复、简要说明。</li><li><strong>自动化</strong>：可自动执行类型检查、ESLint、常规命令；重要变更（构建、路由）需确认；重要操作前保留副本；API 变更优先改 TS 接口；大改前分析依赖与影响。</li><li><strong>优化</strong>：优化未使用 import、重复样式；性能建议；异常与加载状态；内存与异步竞态；复用与架构梳理；设计模式与状态管理。</li><li><strong>测试与追溯</strong>：关键组件与函数单测；提交信息清晰；重大 UI 变更可配变更文档；API/props 变更提供兼容或迁移；修改后任务总结。</li><li><strong>安全与体验</strong>：输入验证与 XSS/CSRF；不暴露敏感信息与内部错误；懒加载与代码分割；交互反馈；错误边界；无障碍与响应式。</li><li><strong>协作</strong>：重要变更前代码审查；沟通与知识分享；持续改进。</li></ul><hr><h2 id="part-2-comment-standards" tabindex="-1">Part 2: Comment Standards <a class="header-anchor" href="#part-2-comment-standards" aria-label="Permalink to &quot;Part 2: Comment Standards&quot;">​</a></h2><ul><li><strong>原则</strong>：必要才写；不用 emoji；与代码同步。</li><li><strong>何时写</strong>：复杂逻辑、业务含义、公共 API（JSDoc）、临时方案/TODO、性能与副作用说明。</li><li><strong>何时不写</strong>：自解释命名、重复函数名的单行说明、简单 getter/setter。</li><li><strong>格式</strong>：单行 <code>//</code>；多行块注释或 JSDoc；公共函数与组件写 @param、@returns、@example。</li><li><strong>语言</strong>：与项目约定一致；同一项目内统一。</li></ul><hr><h2 id="part-3-frontend-standards" tabindex="-1">Part 3: Frontend Standards <a class="header-anchor" href="#part-3-frontend-standards" aria-label="Permalink to &quot;Part 3: Frontend Standards&quot;">​</a></h2><ul><li><strong>通用</strong>：遵循项目架构与 ESLint；组件单一职责，props 类型完整；复用优先；变更范围最小化；API/props 变更需兼容或迁移说明。</li><li><strong>组件与状态</strong>：合理拆分；状态就近；异步与副作用注意清理与竞态；表单与输入校验与防重复。</li><li><strong>样式与布局</strong>：与设计系统一致；响应式与无障碍。</li><li><strong>性能</strong>：懒加载与代码分割；长列表虚拟列表或分页；合理使用 memo/useMemo/useCallback。</li><li><strong>安全与错误</strong>：输入校验与转义；不暴露敏感信息；错误边界与加载/错误状态。</li><li><strong>测试</strong>：关键组件与逻辑单测；E2E 按需；公共组件与 API 有说明或 JSDoc。</li></ul>',10)])])}const u=r(o,[["render",n]]);export{m as __pageData,u as default};
