import{_ as t,c as d,o as a,ag as l}from"./chunks/framework.B-vhMDQ8.js";const p=JSON.parse('{"title":"Skills Guide (Cursor & Claude Code)","description":"","frontmatter":{},"headers":[],"relativePath":"notes/tools/claude/skills-guide.md","filePath":"notes/tools/claude/skills-guide.md"}'),o={name:"notes/tools/claude/skills-guide.md"};function s(r,e,n,c,i,u){return a(),d("div",null,[...e[0]||(e[0]=[l(`<h1 id="skills-guide-cursor-claude-code" tabindex="-1">Skills Guide (Cursor &amp; Claude Code) <a class="header-anchor" href="#skills-guide-cursor-claude-code" aria-label="Permalink to &quot;Skills Guide (Cursor &amp; Claude Code)&quot;">​</a></h1><p>基于 <code>SKILL.md</code> 的技能：教 AI 执行代码审查、commit、查库等专项工作流。</p><hr><h2 id="_1-claude-code-存放位置" tabindex="-1">1. Claude Code：存放位置 <a class="header-anchor" href="#_1-claude-code-存放位置" aria-label="Permalink to &quot;1. Claude Code：存放位置&quot;">​</a></h2><table tabindex="0"><thead><tr><th>类型</th><th>路径</th><th>作用范围</th></tr></thead><tbody><tr><td>个人</td><td><code>~/.claude/skills/&lt;name&gt;/SKILL.md</code></td><td>本机所有项目</td></tr><tr><td>项目</td><td><code>.claude/skills/&lt;name&gt;/SKILL.md</code></td><td>当前项目，可提交 Git</td></tr></tbody></table><p>优先级：企业 &gt; 个人 &gt; 项目。子目录 <code>.claude/skills/</code> 也会被发现（monorepo）。</p><hr><h2 id="_2-目录与格式" tabindex="-1">2. 目录与格式 <a class="header-anchor" href="#_2-目录与格式" aria-label="Permalink to &quot;2. 目录与格式&quot;">​</a></h2><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>&lt;name&gt;/</span></span>
<span class="line"><span>├── SKILL.md       # 必选</span></span>
<span class="line"><span>├── reference.md   # 可选</span></span>
<span class="line"><span>├── examples.md    # 可选</span></span>
<span class="line"><span>└── scripts/       # 可选</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br></div></div><p><strong>SKILL.md</strong>：YAML frontmatter（<code>---</code>）+ Markdown 正文。<code>name</code> 变为 <code>/slash-command</code>；<code>description</code> 供 Claude 判断何时自动加载。</p><hr><h2 id="_3-frontmatter-claude-code" tabindex="-1">3. Frontmatter（Claude Code） <a class="header-anchor" href="#_3-frontmatter-claude-code" aria-label="Permalink to &quot;3. Frontmatter（Claude Code）&quot;">​</a></h2><table tabindex="0"><thead><tr><th>字段</th><th>说明</th></tr></thead><tbody><tr><td>name</td><td>小写/数字/连字符，约 64 字符</td></tr><tr><td>description</td><td>推荐；做什么、何时用</td></tr><tr><td>argument-hint</td><td>参数提示，如 <code>[issue-number]</code></td></tr><tr><td>disable-model-invocation</td><td>true 则仅用户可调用</td></tr><tr><td>user-invocable</td><td>false 则不在 / 菜单显示</td></tr><tr><td>allowed-tools、context、agent</td><td>工具限制、子 agent 等</td></tr></tbody></table><hr><h2 id="_4-流程-claude-code-使用项目内-skills" tabindex="-1">4. 流程：Claude Code 使用项目内 Skills <a class="header-anchor" href="#_4-流程-claude-code-使用项目内-skills" aria-label="Permalink to &quot;4. 流程：Claude Code 使用项目内 Skills&quot;">​</a></h2><ol><li>创建 <code>.claude/skills/&lt;name&gt;/SKILL.md</code>（frontmatter + 正文）。</li><li>在<strong>项目根目录</strong>执行 <code>claude</code> 或 <code>claude-proxy</code>；技能自动发现，无需写进配置。</li><li>对话中输入 <code>/</code> 查看命令，或 <code>/技能名</code> 调用；Claude 也会按 description 自动加载。</li><li>个人全局：复制到 <code>~/.claude/skills/</code>。</li></ol><hr><h2 id="_5-常见问题" tabindex="-1">5. 常见问题 <a class="header-anchor" href="#_5-常见问题" aria-label="Permalink to &quot;5. 常见问题&quot;">​</a></h2><table tabindex="0"><thead><tr><th>现象</th><th>处理</th></tr></thead><tbody><tr><td>403 / Please run /login</td><td>登录问题，用 <code>claude-proxy</code> 启动后 <code>/login</code>；见 <a href="./setup.html">setup</a></td></tr><tr><td>技能未出现在 / 列表</td><td>确认路径 <code>.claude/skills/&lt;name&gt;/SKILL.md</code>，且从项目根启动；检查 frontmatter</td></tr><tr><td>技能列表被截断</td><td>设置 <code>SLASH_COMMAND_TOOL_CHAR_BUDGET</code> 或精简技能</td></tr></tbody></table><hr><h2 id="_6-cursor-存放位置" tabindex="-1">6. Cursor：存放位置 <a class="header-anchor" href="#_6-cursor-存放位置" aria-label="Permalink to &quot;6. Cursor：存放位置&quot;">​</a></h2><table tabindex="0"><thead><tr><th>类型</th><th>路径</th></tr></thead><tbody><tr><td>个人</td><td><code>~/.cursor/skills/&lt;name&gt;/</code></td></tr><tr><td>项目</td><td><code>.cursor/skills/&lt;name&gt;/</code></td></tr></tbody></table><p>不要用 <code>~/.cursor/skills-cursor/</code>。SKILL.md 格式与 Claude Code 类似（name、description、正文）。</p><hr><p><em>规范与技能内容见 <a href="./dev-standards.html">dev-standards</a>。</em></p>`,25)])])}const m=t(o,[["render",s]]);export{p as __pageData,m as default};
