import{_ as t,c as r,o as l,ag as i}from"./chunks/framework.B-vhMDQ8.js";const d=JSON.parse('{"title":"Cursor 开发规则","description":"通用开发规则与最佳实践","frontmatter":{"layout":"doc","title":"Cursor 开发规则","description":"通用开发规则与最佳实践"},"headers":[],"relativePath":"notes/tools/cursor/cursor-rules.md","filePath":"notes/tools/cursor/cursor-rules.md"}'),s={name:"notes/tools/cursor/cursor-rules.md"};function n(a,o,e,g,u,h){return l(),r("div",null,[...o[0]||(o[0]=[i('<h1 id="cursor-开发规则" tabindex="-1">Cursor 开发规则 <a class="header-anchor" href="#cursor-开发规则" aria-label="Permalink to &quot;Cursor 开发规则&quot;">​</a></h1><h2 id="基础交互规则" tabindex="-1">基础交互规则 <a class="header-anchor" href="#基础交互规则" aria-label="Permalink to &quot;基础交互规则&quot;">​</a></h2><ol><li><strong>语言规范</strong>：默认情况下，所有回复都必须是中文，而且需要在开头称呼用户为&quot;亲爱的开发者：&quot;</li><li><strong>任务分解</strong>：复杂需求拆解成小任务，分步实现，每完成一个小任务后再继续</li><li><strong>代码检查</strong>：代码实现前后要仔细检查，确保类型定义完整、组件 props 正确</li><li><strong>功能扩展原则</strong>：在已有功能基础上添加新功能时，必须确保： <ul><li>不影响原有功能和组件复用性</li><li>不添加其他功能、代码、逻辑、文件、配置、依赖</li><li>保持向后兼容性</li></ul></li></ol><h2 id="代码质量与架构" tabindex="-1">代码质量与架构 <a class="header-anchor" href="#代码质量与架构" aria-label="Permalink to &quot;代码质量与架构&quot;">​</a></h2><ol start="5"><li><strong>代码规范</strong>：遵循项目架构设计，保持代码风格与 TypeScript/ESLint 规范一致</li><li><strong>单一职责</strong>：组件设计遵循单一职责原则，不混合多个变更</li><li><strong>设计原则</strong>：在进行组件设计规划时，符合&quot;第一性原理&quot;</li><li><strong>实现原则</strong>：在代码实现时，符合&quot;KISS原则&quot;和&quot;SOLID原则&quot;</li><li><strong>代码复用</strong>：优先使用现有组件库和 hooks，避免重复代码</li><li><strong>依赖管理</strong>：不引入不必要的依赖，优先使用项目已有库</li><li><strong>代码可读性</strong>：确保代码可读性，复杂逻辑添加注释，组件 props 类型详细定义</li><li><strong>变更范围</strong>：代码变更范围最小化，避免修改公共组件、全局状态</li><li><strong>逻辑验证</strong>：实现后进行基本逻辑自检，确保状态管理与生命周期正确</li><li><strong>API 设计</strong>：如有疑问，先询问再修改，不要擅自改变组件 API 设计</li></ol><h2 id="文档与输出规则" tabindex="-1">文档与输出规则 <a class="header-anchor" href="#文档与输出规则" aria-label="Permalink to &quot;文档与输出规则&quot;">​</a></h2><ol start="15"><li><strong>文档生成策略</strong>：用户不希望在解决问题后自动生成修复文档、验证指南、总结文档等文档</li><li><strong>文档触发条件</strong>：只有当用户明确要求&quot;写文档&quot;、&quot;生成文档&quot;、&quot;需要文档&quot;、&quot;写个验证指南&quot;等关键词时，才生成相应文档</li><li><strong>默认行为</strong>：默认行为应该是：分析问题、修复代码、简要说明修复内容，但不生成任何文档文件</li></ol><h2 id="自动化执行与安全策略" tabindex="-1">自动化执行与安全策略 <a class="header-anchor" href="#自动化执行与安全策略" aria-label="Permalink to &quot;自动化执行与安全策略&quot;">​</a></h2><ol start="18"><li><strong>自动执行操作</strong>：自动执行无需严格确认的操作，提高效率：</li></ol><ul><li>自动执行 TypeScript 类型检查、ESLint 验证</li><li>文件操作（创建组件、修改样式文件）无需额外确认</li><li>常规命令（如 npm install、启动开发服务器）可直接执行</li><li>涉及构建配置、路由修改等重要变更仍需确认</li></ul><ol start="19"><li><strong>重要操作保护</strong>：重要操作（修改全局状态、路由配置）应先保留副本</li><li><strong>API 接口变更</strong>：涉及 API 接口变更，优先修改 TypeScript 接口定义</li><li><strong>影响范围分析</strong>：执行影响较大的修改前，自动检测组件依赖关系，分析影响范围</li></ol><h2 id="代码质量优化" tabindex="-1">代码质量优化 <a class="header-anchor" href="#代码质量优化" aria-label="Permalink to &quot;代码质量优化&quot;">​</a></h2><ol start="22"><li><strong>代码优化</strong>：代码生成后，自动优化（移除未使用 imports、合并重复样式）</li><li><strong>性能优化</strong>：对可能影响性能的代码（如不必要的重渲染、大型循环）提供优化建议</li><li><strong>错误处理</strong>：确保异常处理和加载状态管理，防止白屏和渲染错误</li><li><strong>内存管理</strong>：注意内存泄漏问题，及时清理事件监听器、定时器等资源</li><li><strong>异步处理</strong>：正确处理异步操作，避免竞态条件和状态不一致</li></ol><h2 id="架构感知与设计" tabindex="-1">架构感知与设计 <a class="header-anchor" href="#架构感知与设计" aria-label="Permalink to &quot;架构感知与设计&quot;">​</a></h2><ol start="27"><li><strong>组件复用</strong>：优先分析现有组件库与状态管理模式，避免创建冗余组件</li><li><strong>功能复用</strong>：添加功能时，优先考虑复用 hooks、contexts 或现有组件</li><li><strong>架构梳理</strong>：如遇架构不清晰，先梳理组件层次与数据流，再执行修改</li><li><strong>设计模式</strong>：合理使用设计模式，提高代码的可维护性和扩展性</li><li><strong>状态管理</strong>：合理设计状态管理，避免状态冗余和循环依赖</li></ol><h2 id="测试与验证" tabindex="-1">测试与验证 <a class="header-anchor" href="#测试与验证" aria-label="Permalink to &quot;测试与验证&quot;">​</a></h2><ol start="32"><li><strong>单元测试</strong>：为关键组件和函数编写单元测试</li><li><strong>集成测试</strong>：确保组件间的集成正常工作</li><li><strong>边界测试</strong>：测试边界条件和异常情况</li><li><strong>性能测试</strong>：关注组件渲染性能和内存使用</li></ol><h2 id="代码变更的可追溯性" tabindex="-1">代码变更的可追溯性 <a class="header-anchor" href="#代码变更的可追溯性" aria-label="Permalink to &quot;代码变更的可追溯性&quot;">​</a></h2><ol start="36"><li><strong>提交信息</strong>：提供清晰的 commit 信息，描述组件变更和影响范围</li><li><strong>变更文档</strong>：对于 UI 组件重大调整，生成变更文档与截图对比</li><li><strong>兼容性方案</strong>：API 或 props 变更时，提供向下兼容方案或迁移指南</li><li><strong>项目分析</strong>：执行任务前，先分析项目结构和组件关系文档</li><li><strong>任务总结</strong>：每次修改后，生成任务总结，说明组件变更和状态管理调整</li><li><strong>文档维护</strong>：手动维护组件文档与设计系统说明，确保长期可维护性</li></ol><h2 id="安全与最佳实践" tabindex="-1">安全与最佳实践 <a class="header-anchor" href="#安全与最佳实践" aria-label="Permalink to &quot;安全与最佳实践&quot;">​</a></h2><ol start="42"><li><strong>输入验证</strong>：对所有用户输入进行验证和清理</li><li><strong>XSS 防护</strong>：避免直接插入用户内容到 DOM 中</li><li><strong>CSRF 防护</strong>：在涉及状态变更的操作中使用 CSRF 令牌</li><li><strong>敏感信息</strong>：不在客户端代码中暴露敏感信息（API 密钥、密码等）</li><li><strong>错误信息</strong>：不向用户暴露系统内部错误信息</li></ol><h2 id="性能与用户体验" tabindex="-1">性能与用户体验 <a class="header-anchor" href="#性能与用户体验" aria-label="Permalink to &quot;性能与用户体验&quot;">​</a></h2><ol start="47"><li><strong>加载优化</strong>：实现懒加载和代码分割，减少初始加载时间</li><li><strong>交互反馈</strong>：为用户操作提供及时的视觉反馈</li><li><strong>错误边界</strong>：实现错误边界，防止单个组件错误影响整个应用</li><li><strong>无障碍访问</strong>：确保组件支持键盘导航和屏幕阅读器</li><li><strong>响应式设计</strong>：确保组件在不同屏幕尺寸下正常工作</li></ol><h2 id="团队协作" tabindex="-1">团队协作 <a class="header-anchor" href="#团队协作" aria-label="Permalink to &quot;团队协作&quot;">​</a></h2><ol start="52"><li><strong>代码审查</strong>：重要变更前进行代码审查</li><li><strong>沟通协作</strong>：与团队成员保持良好沟通，及时同步变更信息</li><li><strong>知识分享</strong>：将解决方案和最佳实践分享给团队</li><li><strong>持续改进</strong>：根据项目发展持续优化开发流程和规范</li></ol>',25)])])}const q=t(s,[["render",n]]);export{d as __pageData,q as default};
